                        showAlert(Alert.AlertType.ERROR, "로그인 실패", "아이디 또는 비밀번호가 올바르지 않습니다.");
                    }
                }
            } catch (Exception ex) {
                showAlert(Alert.AlertType.ERROR, "오류", "로그인 중 오류가 발생했습니다: " + ex.getMessage());
            }
